import axios from "axios";
import { useEffect, useState } from "react";

function Detail() {
    const [episode, setEpisode] = useState({});
    const [orjCharacters, setOrjCharacters] = useState([]);
    const [characters, setCharacters] = useState([]);
    const [pages, setPages] = useState(0);
    const [pageNumber, setPageNumber] = useState(1);

    useEffect(() => {
        getUrlParameter();
    }, [])

    function getUrlParameter() {
        const lastIndex = document.URL.lastIndexOf("=") + 1;
        const lenght = document.URL.length;
        // setId(document.URL.substring(lastIndex,lenght));
        getEpisodeInformation(document.URL.substring(lastIndex, lenght));
    }

    async function getEpisodeInformation(id) {
        const result = await axios.get(`https://rickandmortyapi.com/api/episode/${id}`)
        setEpisode(result.data);

        //Karakterlerin API isteği ile bilgilerini almak için
        const characterPromises = result.data.characters.map(url => axios.get(url));
        const characterResults = await Promise.all(characterPromises);
        
        const characterList = characterResults.map(response => response.data);
        setOrjCharacters(characterList);

        //Sayfa sayısını tespit için
        const pageNumbers = Math.ceil(characterList.length / 5);
        const numbers = [];
        for (let i = 1; i <= pageNumbers; i++){
            numbers.push(i);           
        }

        setPages(numbers);

        const list = [];
        const startNumber = (pageNumber - 1) * 8;
        const endNumber = (startNumber + 8) > characterList.length ? characterList.length : (startNumber + 8);
        for (let i = startNumber; i < endNumber; i++) {
            const element = characterList[i];
            list.push(element);
        }

        setCharacters(list);
    }

    return (
        <>
            <div className="text-center">
                <h1>Episode: {episode.episode} - {episode.name}</h1>
                <p>Date: {episode.air_date}</p>
                <h3>Character List</h3>
            </div>
            <hr />
            <div className="row">
                {characters.map((val, i) =>
                    <div className="col-3">
                        <div className="card">
                            <div className="card-header">
                                <img src={val.image} style={{ width: "100%" }} />
                            </div>
                            <div className="card-body d-flex align-items-center" style={{ flexDirection: "column" }}>
                                <h6>{val.name}</h6>
                                <span>{val.species}</span>
                            </div>
                        </div>
                    </div>
                )}
            </div>

            <div className='d-flex justify-content-center mt-2'>
        <nav aria-label="Page navigation example">
          <ul className="pagination">
            <li className="page-item"><a className="page-link" href="#" onClick={() => getAllEpisode(pageNumber - 1)}>Previous</a></li>
            {pages.map((val, i) => {
              if (val === pageNumber) {
                return (<li key={i} className="page-item active"><a className="page-link" href="#" onClick={() => getAllEpisode(val)}>{val}</a></li>)
              } else {
                return (<li key={i} className="page-item"><a className="page-link" href="#" onClick={() => getAllEpisode(val)}>{val}</a></li>)
              }
            }
            )}
            <li className="page-item"><a className="page-link" href="#" onClick={() => getAllEpisode(pageNumber + 1)}>Next</a></li>
          </ul>
        </nav>
      </div>
        </>
    )
}

export default Detail;
