import axios from 'axios';
import { useEffect, useState } from 'react';

function App() {
  const [list, useList] = useState([]);
  const [listInformation, useListInformation] = useState([]);

  useEffect(()=> {
    getAllEpisode();
  }, [])

  function getAllEpisode(){
    axios.get("https://rickandmortyapi.com/api/episode?page=1").then(res=> {
      console.log(res.data.results);
      this.useList(res?.data?.results);
      this.useListInformation(res.data.info);
    });
  }

  return (
    <div className='container'>
      <h1>App Component</h1>
      <div className='row'>
        {list.map((value, index)=> 
        <div className='col-3' key={index}>
          <div className='card'>
            <div className='card-header'>
              <img src="https://m.media-amazon.com/images/I/71WicKDKUgL._AC_UF1000,1000_QL80_.jpg" width={100}/>
            </div>
            <div className='card-body'>
              <h1>Name: {value.name}</h1>
              <p>Episode: {value.episode}</p>
              <p>Date: {value.created}</p>
            </div>
          </div>
        </div>)}        
      </div>
    </div>

  );
}

export default App;


