import axios from 'axios';
import { useEffect, useState } from 'react';

function App() {
  const [list, useList] = useState([]);
  const [listInformation, useListInformation] = useState([]);

  useEffect(()=> {
    getAllEpisode();
  }, [])

  function getAllEpisode(){
    axios.get("https://rickandmortyapi.com/api/episode?page=1").then(res=> {
      useList(res.data.results);
      useListInformation(res.data.info);
    });
  }

  return (
    <div className='container'>
      <h1>App Component</h1>
      <div className='row'>
        {list.map((value, index)=> 
        <div className='col-3'>
          <div className='card'>
            <div className='card-header'>
              <h1>{value.episode}</h1>
            </div>
            <div className='card-body'>

            </div>
          </div>
        </div>)}        
      </div>
    </div>

  );
}

export default App;


